mapper.select:
select * from liepin.role
left join liepin.user on user.role_id = role.id
where user.id = #{userId}

select count(work_log.id) from work_log
where dlt = 'NO'
and if(weekday(date(now())) < 5,
if(weekday(date(now())) = 0,date(create_time) = date(date_sub(now(),interval 3 day)),date(create_time) = date(date_sub(now(),interval 1 day))),
1 = 1)
and user_id = #{userId}
