mapper.select:
select temp.id as infoId, count(temp.id) as num, enterprise_info.id, enterprise_info.name, enterprise_info.province, enterprise_info.city, enterprise_info.legal_representative, enterprise_info.create_time, (select throwback_reason from liepin.enterprise_throwback_history where enterprise_throwback_history.info_id = infoId order by throwback_time desc limit 1) from enterprise_info inner join (select enterprise_info.id from enterprise_info where enterprise_info.dlt = 'NO' and enterprise_info.is_private = 'NO'
and enterprise_info.county = #{req.county}
and enterprise_info.city = #{req.city}
and enterprise_info.province = #{req.province}
and enterprise_info.phone like concat('%',#{req.phone},'%')
and enterprise_info.name like concat('%',#{req.name},'%')
order by enterprise_info.id) as temp on temp.id = enterprise_info.id
limit #{offset},#{req.pageSize}

select count(enterprise_info.id) from liepin.enterprise_info inner join liepin.enterprise_ocean on enterprise_ocean.info_id = enterprise_info.id where enterprise_ocean.dlt = 'NO' and enterprise_ocean.is_private = 'NO'
and enterprise_info.name like concat('%',#{req.name},'%')
and enterprise_info.province = #{req.province}
and enterprise_info.city = #{req.city}
and enterprise_info.phone like concat('%',#{req.phone},'%')
