mapper.select:
select temp.id as infoId, enterprise_info.id, enterprise_info.name, enterprise_info.province, enterprise_info.city, enterprise_info.county, enterprise_info.legal_representative, enterprise_info.create_time, enterprise_info.contact, enterprise_info.phone, enterprise_info.email, (select throwback_reason from liepin.enterprise_throwback_history where enterprise_throwback_history.info_id = infoId order by enterprise_throwback_history.create_time desc limit 1) as throwback_reason from enterprise_info inner join (select enterprise_info.id from enterprise_info where enterprise_info.dlt = 'NO' and enterprise_info.is_private = 'NO'
and enterprise_info.county = #{req.county}
and enterprise_info.city = #{req.city}
and enterprise_info.province = #{req.province}
and enterprise_info.phone like concat('%',#{req.phone},'%')
and enterprise_info.name like concat('%',#{req.name},'%')
order by enterprise_info.id desc) as temp on temp.id = enterprise_info.id
limit #{offset},#{req.pageSize}

select count(enterprise_info.id) from enterprise_info
where enterprise_info.dlt = 'NO'
and enterprise_info.is_private = 'NO'

    and enterprise_info.county = #{req.county}


    and enterprise_info.city = #{req.city}


    and enterprise_info.province = #{req.province}


    and enterprise_info.phone like concat('%',#{req.phone},'%')


    and enterprise_info.name like concat('%',#{req.name},'%')

order by enterprise_info.id
