mapper.select:
select enterprise_private.id, enterprise_info.province, enterprise_info.city, enterprise_info.county, enterprise_info.legal_representative, enterprise_private.create_time from enterprise_private inner join (select enterprise_private.id from enterprise_private left join enterprise_info on enterprise_info.id = enterprise_private.info_id where enterprise_private.throwback = 'NO' and enterprise_private.status = 'NOT_CONTACT'
and enterprise_private.user_id = #{userId}
and enterprise_info.phone = #{req.phone}
and enterprise_info.contact = #{req.contact}
and enterprise_info.county = #{req.county}
and enterprise_info.city = #{req.city}
and enterprise_info.province = #{req.province}
and enterprise_info.name like concat('%',#{req.name},'%')
order by enterprise_private.id desc ) as temp on temp.id = enterprise_private.id left join enterprise_info on enterprise_private.info_id = enterprise_info.id
limit #{offset},#{req.pageSize}

select count(enterprise_private.id)
from enterprise_private
left join enterprise_info
on enterprise_info.id = enterprise_private.info_id
where enterprise_private.throwback = 'NO'
and enterprise_private.status = 'NOT_CONTACT'

    and enterprise_private.user_id = #{userId}


    and enterprise_info.phone = #{req.phone}


    and enterprise_info.contact = #{req.contact}


    and enterprise_info.county = #{req.county}


    and enterprise_info.city = #{req.city}


    and enterprise_info.province = #{req.province}


    and enterprise_info.name like concat('%',#{req.name},'%')

select enterprise_throwback_history.id, enterprise_info.name, enterprise_throwback_history.throwback_reason, enterprise_throwback_history.remark, enterprise_private.user_id, user.name as username, enterprise_throwback_history.create_time as sendAuditTime, enterprise_throwback_history.audit_status, enterprise_throwback_history.audit_time from enterprise_throwback_history inner join ( select enterprise_throwback_history.id from enterprise_throwback_history left join enterprise_private on enterprise_private.id = enterprise_throwback_history.private_id where enterprise_throwback_history.audit_status = #{req.auditStatus}
and enterprise_private.user_id = #{userId}
and enterprise_throwback_history.create_time >= #{req.startTime}
and enterprise_throwback_history.create_time <= #{req.endTime}
order by enterprise_throwback_history.create_time desc ) as temp on enterprise_throwback_history.id = temp.id left join enterprise_private on enterprise_throwback_history.private_id = enterprise_private.id left join enterprise_info on enterprise_info.id = enterprise_throwback_history.info_id left join user on user.id = enterprise_private.user_id
limit #{offset},#{req.pageSize}

select count(enterprise_throwback_history.id) from enterprise_throwback_history
left join enterprise_private
on enterprise_private.id = enterprise_throwback_history.private_id
where enterprise_throwback_history.audit_status = #{req.auditStatus}

    and enterprise_private.user_id = #{userId}


    and enterprise_throwback_history.create_time >= #{req.startTime}


    and enterprise_throwback_history.create_time <= #{req.endTime}
