mapper.select:
select id, dept, private_id, enterprise_name, from_id, (select name from user where user.id = from_id) as fromName, to_id, (select name from user where user.id = to_id) as toName, remark, create_time, audit_status as status, if(audit_status is not 'WAIT',audit_id,null) as auditId, (select name from user where user.id = auditId) as auditName, audit_remark from send_to where dept = 'ENTERPRISE' and dlt = 'NO'
and to_id = #{req.toId}
and enterprise_name = #{req.enterpriseName}
and from_id = #{userId} and audit_status = #{req.status}
and to_id = #{userId} and audit_status = 'PASS'
limit #{offset},#{req.pageSize}

select
    count(id)
from send_to
where dept = 'ENTERPRISE'
and dlt = 'NO'

    and to_id = #{req.toId}


    and enterprise_name = #{req.enterpriseName}


    and from_id = #{userId}
    and audit_status = #{req.status}


    and to_id = #{userId}
    and audit_status = 'PASS'

select
    id,
    dept,
    private_id,
    enterprise_name,
    from_id,
    (select name from user where user.id = from_id) as fromName,
    to_id,
    (select name from user where user.id = to_id) as toName,
    remark,
    create_time,
    audit_status as status,
    if(audit_status is not 'WAIT',audit_id,null) as auditId,
    (select name from user where user.id = auditId) as auditName,
    audit_remark
from send_to
where dlt = 'NO'

    and dept = #{req.dept}


    and enterprise_name = #{req.enterpriseName}


    and from_id = #{req.fromId}


    and from_id = #{req.toId}


    and audit_status = #{req.status}



    limit #{offset},#{req.pageSize}

