mapper.select:
select talent_private.id, talent_info.name, talent_info.sex, talent_info.intention, talent_info.type, talent_info.phone, talent_private.create_time from talent_private inner join (select talent_private.id from talent_private left join talent_info on talent_info.id = talent_private.info_id where talent_private.throwback = 'NO' and talent_private.status = 'NOT_CONTACT' and talent_private.send_status = 'NO'
and talent_private.user_id = #{userId}
and talent_info.phone = #{req.phone}
and talent_info.intention = #{req.intention}
and talent_info.name = #{req.name}
order by talent_private.id desc ) as temp on temp.id = talent_private.id left join talent_info on talent_private.info_id = talent_info.id
limit #{offset},#{req.pageSize}

select count(talent_private.id)
from talent_private
left join talent_info
on talent_info.id = talent_private.info_id
where talent_private.throwback = 'NO'
and talent_private.status = 'NOT_CONTACT'
and talent_private.send_status = 'NO'

    and talent_private.user_id = #{userId}


    and talent_info.phone = #{req.phone}


    and talent_info.intention = #{req.intention}


    and talent_info.name = #{req.name}

select talent_throwback_history.id, talent_info.name, talent_throwback_history.throwback_reason, talent_throwback_history.remark, talent_private.user_id, user.name as username, talent_throwback_history.create_time as sendAuditTime, talent_throwback_history.audit_status, talent_throwback_history.audit_time from talent_throwback_history inner join ( select talent_throwback_history.id from talent_throwback_history left join talent_private on talent_private.id = talent_throwback_history.private_id where talent_throwback_history.audit_status = #{req.auditStatus}
and talent_private.user_id = #{userId}
and talent_throwback_history.create_time >= #{req.startTime}
and talent_throwback_history.create_time <= #{req.endTime}
order by talent_throwback_history.create_time desc ) as temp on talent_throwback_history.id = temp.id left join talent_private on talent_throwback_history.private_id = talent_private.id left join talent_info on talent_info.id = talent_throwback_history.info_id left join user on user.id = talent_private.user_id
limit #{offset},#{req.pageSize}

select count(talent_throwback_history.id) from talent_throwback_history
left join talent_private
on talent_private.id = talent_throwback_history.private_id
where talent_throwback_history.audit_status = #{req.auditStatus}

    and talent_private.user_id = #{userId}


    and talent_throwback_history.create_time >= #{req.startTime}


    and talent_throwback_history.create_time <= #{req.endTime}

select temp.privateId as id, name, sex, phone, intention, intention, datediff(now(),(select time from talent_private_followup where talent_private_followup.dlt = 'NO' and talent_private_followup.private_id = temp.privateId order by id desc limit 1)) as lostContactDays from talent_info inner join (select id as privateId,info_id as infoId from talent_private where user_id = #{userId} and send_status = 'NO' and throwback = 'NO' and status = 'FOLLOWUP' order by id desc ) as temp on temp.infoId = talent_info.id where dlt = 'NO'
and name = #{req.name}
and phone = #{req.phone}
and sex = #{req.sex}
and intention = #{req.intention}
limit #{offset},#{req.pageSize}

select
count(talent_info.id)
from talent_info
inner join (select id as privateId,info_id as infoId from talent_private
where user_id = #{userId}
and send_status = 'NO'
and throwback = 'NO'
and status = 'FOLLOWUP'
order by id desc ) as temp
on temp.infoId = talent_info.id
where dlt = 'NO'

    and name = #{req.name}


    and phone = #{req.phone}


    and sex = #{req.sex}


    and intention = #{req.intention}
