mapper.select:
select talent_info.id, talent_info.name, talent_info.type, talent_info.agency_id as agencyId, (select agency.enterprise_name from agency where agency.id = agencyId) as agencyName, talent_info.phone, talent_info.sex, talent_info.certificate_type, talent_info.certificate_major, talent_info.hire_type from talent_info inner join (select id from talent_info where dlt = 'NO' and is_private = 'NO'
and name = #{req.name}
and phone = #{req.phone}
and sex = #{req.sex}
and type = #{req.type}
and certificate_type = #{req.certificateType}
and certificate_major = #{req.certificateMajor}
order by id desc ) as temp on temp.id = talent_info.id
limit #{offset},#{req.pageSize}

select count(talent_info.id) from talent_info
where dlt = 'NO'
and is_private = 'NO'

    and name = #{req.name}


    and phone = #{req.phone}


    and sex = #{req.sex}


    and type = #{req.type}


    and certificate_type = #{req.certificateType}


    and certificate_major = #{req.certificateMajor}

order by id desc
