select id, dept, private_id, talent_name, from_id, (select name from user where user.id = from_id) as fromName, to_id, (select name from user where user.id = to_id) as toName, remark, create_time, audit_status as status, if(audit_status != 'WAIT',audit_id,null) as auditId, (select name from user where user.id = auditId) as auditName, audit_remark from send_to where dept = 'TALENT' and dlt = 'NO'
and to_id = #{req.toId}
and talent_name = #{req.talentName}
and from_id = #{userId} and audit_status = #{req.status}
and to_id = #{userId} and audit_status = 'PASS'
limit #{offset},#{req.pageSize}

