mapper.select:
select username
from liepin.user
where id = #{id}

select count(*) from liepin.agency where liepin.agency.audit_status like concat('%',#{req.auditStatus},'%') and liepin.agency.dlt = 'NO'
and liepin.agency.enterprise_name like concat('%',#{req.enterprise_name},'%')
and liepin.agency.province = #{req.province}
and liepin.agency.city = #{req.city}

select id,enterprise_name,person,province,city,detail_addr, (select name from liepin.user where liepin.user.id = liepin.agency.create_id) as create_id, tel,audit_status,create_time,remark from liepin.agency where liepin.agency.audit_status like concat('%',#{req.auditStatus},'%') and liepin.agency.dlt = 'NO'
and liepin.agency.enterprise_name like concat('%',#{req.enterpriseName},'%')
and liepin.agency.province = #{req.province}
and liepin.agency.city = #{req.city}
order by liepin.agency.id asc
limit #{offset},#{req.pageSize}
