mapper.select:
select distinct liepin.work_log.id as log_id,username,name,liepin.user.id,liepin.role.role_code as role, liepin.work_log.update_time,liepin.work_log_detail.phone_num,liepin.work_log_detail.wechat_num, liepin.work_log_detail.intentional_customer, (select count(*) from liepin.work_log_problem,liepin.work_log where liepin.work_log_problem.dlt = 'NO' and liepin.work_log.id = liepin.work_log_problem.id and liepin.work_log.user_id = liepin.user.id ) as unfinishedProblemNum from liepin.work_log left join liepin.work_log_problem on liepin.work_log.id =liepin.work_log_problem.id join liepin.user on work_log.user_id = liepin.user.id join liepin.work_log_detail on work_log.id = work_log_detail.id join liepin.role on user.role_id = role.id
where liepin.user.dlt='NO'
and liepin.user.id = #{req.id}
and liepin.user.name = like concat('%',#{req.name},'%')
and liepin.user.username like concat('%',#{req.username},'%')
and liepin.user.role_id = #{req.role}
and liepin.work_log_problem.solved = #{req.solved}
order by liepin.user.id asc
limit #{offset},#{req.pageSize}

select COUNT(distinct liepin.work_log_problem.id)
from liepin.work_log left join liepin.work_log_problem on liepin.work_log.id =liepin.work_log_problem.id
join liepin.user on work_log.user_id = liepin.user.id
join liepin.role on user.role_id = role.id
where liepin.user.dlt='NO'
  and liepin.work_log.dlt = 'NO'

    and liepin.user.id = #{req.id}


    and liepin.user.username like concat('%',#{req.username},'%')


    and liepin.user.role_id = #{req.role}


    and liepin.work_log_problem.solved = #{req.solved}

order by liepin.user.id asc


    limit #{offset},#{req.pageSize}
